query ProfileSummary($username: String!) {
  user(login: $username) {
    __typename
    avatarUrl
    name
    login
    email
    followers {
      __typename
      totalCount
    }
    following {
      __typename
      totalCount
    }
    pinnedItems(first: 3) {
      __typename
      nodes {
        __typename
        ... on Repository {
          __typename
          owner {
            __typename
            login
            avatarUrl
          }
          name
          description
          stargazerCount
          languages(first: 1) {
            __typename
            nodes {
              __typename
              name
              color
            }
          }
        }
      }
    }
    topRepositories(first: 10, orderBy: {field: STARGAZERS, direction: DESC}) {
      __typename
      nodes {
        __typename
        owner {
          __typename
          login
          avatarUrl
        }
        name
        description
        stargazerCount
        languages(first: 1) {
          __typename
          nodes {
            __typename
            name
            color
          }
        }
      }
    }
    starredRepositories(first: 10, orderBy: {field: STARRED_AT, direction: ASC}) {
      __typename
      nodes {
        __typename
        owner {
          __typename
          login
          avatarUrl
        }
        name
        description
        stargazerCount
        languages(first: 1) {
          __typename
          nodes {
            __typename
            name
            color
          }
        }
      }
    }
  }
}
